import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function App() {
  const [event,setevent]=useState("");
  const [item,setitem]=useState([]);
  const changer=(change)=>{
    setevent(change.target.value);
  };
  const AddTask=()=>{
    setitem((oldvalue)=>{
      return [...oldvalue,event];
    });
  };
  const deltetask=(val,index)=>{
    console.log(val,index)
    setitem((oldvalue)=>{
      return(oldvalue.filter((val,id)=>{
        return id!==index;
      }));
    });
  };
  return (
    <div className="main-pad">
      <h1>To-do-List</h1>
      <div id='input-data'>
        <input type='text' name='task' placeholder='Add a Task' value={event} onChange={changer}></input>
        <button onClick={AddTask}>+</button>
      </div> 
        {item.map((itvalue,index)=>{
          return <div id='input-data'>
          <button id='cancel' onClick={deltetask(itvalue,id)}>X</button>
          <p>{itvalue}</p></div>
        })}
    </div>
  );
}

export default App;
